import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { serviceData, cityData } from './serviceData';
import './ServicePage.css';

const ServicePage = () => {
  const { service, city } = useParams();
  
  const serviceInfo = serviceData[service] || serviceData['office-housekeeping'];
  const cityInfo = cityData[city] || cityData['nagpur'];

  return (
    <div className="service-page">
      {/* Hero Section */}
      <section className="service-hero">
        <div className="service-hero-overlay"></div>
        <div className="container service-hero-content">
          <h1>{serviceInfo.title}</h1>
          <p>Quality Housekeeping Services in {cityInfo.name}</p>
        </div>
      </section>

      {/* Content Section */}
      <section className="service-content-section">
        <div className="container">
          <div className="service-content">
            <h2>About {serviceInfo.title}</h2>
            <p>{serviceInfo.content}</p>
            
            <div className="service-features">
              <h3>Why Choose Us?</h3>
              <ul>
                <li>✓ Professional and trained staff</li>
                <li>✓ Eco-friendly cleaning products</li>
                <li>✓ Flexible scheduling</li>
                <li>✓ Affordable pricing</li>
                <li>✓ 100% satisfaction guaranteed</li>
              </ul>
            </div>

            <div className="service-contact">
              <h3>Get in Touch</h3>
              <p><strong>Location:</strong> {cityInfo.name}</p>
              <p><strong>Phone:</strong> <a href={`tel:${cityInfo.phone}`}>{cityInfo.phone}</a></p>
              <p><strong>Address:</strong> {cityInfo.address}</p>
              <Link to="/quote" className="btn btn-primary">Get Free Quote</Link>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ServicePage;
